import React, { useEffect, useRef, useState } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = ({ setDataUrl }) => {
    const canvasRef = useRef(null);
    const [fabricCanvas, setFabricCanvas] = useState(null);
    const [dataUrl, setDataUrlLocal] = useState("");

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleDrop = (e) => {
        e.preventDefault();
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        if (type === 'library') {
            fabric.Image.fromURL(
                url,
                (img) => {
                    img.set({
                        selectable: true,
                        scalable: true,
                        hasControls: true,
                    });
                    img.scale(0.25);
                    img.setCoords();
                    addDeleteButton(img); // 添加删除按钮
                    fabricCanvas.add(img);
                },
                { crossOrigin: 'anonymous' }
            );
        }
    };

    const addDeleteButton = (obj) => {
        const deleteButton = new fabric.Text('x', {
            fontFamily: 'Arial',
            fontSize: 20,
            fill: 'red',
            originX: 'right',
            originY: 'top',
            selectable: false,
            evented: false,
        });
        deleteButton.set({
            top: obj.top,
            left: obj.left + obj.width,
        });
        deleteButton.on('mousedown', () => {
            fabricCanvas.remove(obj);
            fabricCanvas.remove(deleteButton);
            saveCanvasToDataUrl();
        });
        obj.deleteButton = deleteButton;
        fabricCanvas.add(deleteButton);
        fabricCanvas.bringToFront(deleteButton);
    };

    useEffect(() => {
        if (!fabricCanvas) {
            const newCanvas = new fabric.Canvas(canvasRef.current, {
                width: window.innerHeight * 0.6,
                height: window.innerHeight * 0.6,
                selection: true,
                preserveObjectStacking: true,
                interactive: true,
            });
            setFabricCanvas(newCanvas);
        }
    }, []);

    const saveCanvasToDataUrl = () => {
        if (fabricCanvas) {
            const dataURL = fabricCanvas.toDataURL({
                format: 'png',
                multiplier: 1,
                quality: 1
            });
            setDataUrlLocal(dataURL);
            setDataUrl(dataURL);
        }
    };

    useEffect(() => {
        saveCanvasToDataUrl();

        const canvasStateChangeListener = () => {
            saveCanvasToDataUrl();
        };

        if (fabricCanvas) {
            fabricCanvas.on('object:added', canvasStateChangeListener);
            fabricCanvas.on('object:removed', canvasStateChangeListener);
            fabricCanvas.on('selection:cleared', removeDeleteButton);
        }

        return () => {
            if (fabricCanvas) {
                fabricCanvas.off('object:added', canvasStateChangeListener);
                fabricCanvas.off('object:removed', canvasStateChangeListener);
                fabricCanvas.off('selection:cleared', removeDeleteButton);
            }
        };
    }, [fabricCanvas, setDataUrl]);

    const removeDeleteButton = () => {
        if (fabricCanvas) {
            fabricCanvas.getObjects().forEach(obj => {
                if (obj.deleteButton) {
                    fabricCanvas.remove(obj.deleteButton);
                    delete obj.deleteButton;
                }
            });
        }
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh', position: 'relative', backgroundColor: 'white' }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
        </div>
    );
};

export default FabricCanvas;
