import React, { useEffect, useRef, useState } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasContainerRef = useRef(null);
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);
    const [eventListenersAdded, setEventListenersAdded] = useState(false);

    useEffect(() => {
        fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
            width: window.innerHeight * 0.6,
            height: window.innerHeight * 0.6,
        });

        // 添加事件监听器
        if (!eventListenersAdded) {
            canvasRef.current.addEventListener('dragover', handleDragOver);
            canvasRef.current.addEventListener('drop', handleDrop);
            setEventListenersAdded(true);
        }

        return () => {
            // 移除事件监听器
            canvasRef.current.removeEventListener('dragover', handleDragOver);
            canvasRef.current.removeEventListener('drop', handleDrop);
        };
    }, [eventListenersAdded]);

    // 处理dragover事件
    const handleDragOver = (e) => {
        e.preventDefault();
    };

    // 处理drop事件
    const handleDrop = (e) => {
        e.preventDefault();

        // 获取拖拽的数据
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        // 如果是来自素材库的拖拽数据
        if (type === 'library') {
            // 创建fabric.Image对象
            fabric.Image.fromURL(url, (img) => {
                // 获取鼠标在画布容器中的相对位置
                const offsetX = e.clientX - canvasContainerRef.current.getBoundingClientRect().left;
                const offsetY = e.clientY - canvasContainerRef.current.getBoundingClientRect().top;

                // 设置图片位置
                img.set({
                    left: offsetX - img.width / 2, // 让鼠标指针位于图像中心
                    top: offsetY - img.height / 2,
                });

                // 添加图片到画布
                fabricCanvasRef.current.add(img);
            });
        }
    };

    return (
        <div
            ref={canvasContainerRef}
            style={{
                position: 'relative',
                width: '60vh',
                height: '60vh',
            }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ border: '1px solid black', maxWidth: '100%', maxHeight: '100%',backgroundColor:'white' }}
            ></canvas>
        </div>
    );
};

export default FabricCanvas;
