import React, { useRef, useEffect } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = new fabric.Canvas(canvasRef.current, {
            width: 800,
            height: 600,
            backgroundColor: '#f0f0f0'
        });

        // 拖放图片到画布上
        canvasRef.current.addEventListener('dragover', (e) => {
            e.preventDefault();
        });

        canvasRef.current.addEventListener('drop', (e) => {
            e.preventDefault();
            const file = e.dataTransfer.files[0];
            const reader = new FileReader();
            reader.onload = (event) => {
                const imgObj = new fabric.Image.fromURL(event.target.result, (img) => {
                    img.set({
                        left: e.clientX - canvasRef.current.getBoundingClientRect().left,
                        top: e.clientY - canvasRef.current.getBoundingClientRect().top,
                        scaleX: 0.2, // 缩放初始大小
                        scaleY: 0.2
                    });
                    canvas.add(img);
                });
            };
            reader.readAsDataURL(file);
        });

        // 使得图片可拖动
        canvas.on('object:moving', (e) => {
            const obj = e.target;
            // 如果图片超出画布范围，限制其拖动
            if (obj.getBoundingRect().top < 0 ||
                obj.getBoundingRect().left < 0 ||
                obj.getBoundingRect().top + obj.getBoundingRect().height > canvas.height ||
                obj.getBoundingRect().left + obj.getBoundingRect().width > canvas.width) {
                obj.setCoords();
            }
        });

        // 使得图片可缩放
        canvas.on('object:scaling', (e) => {
            const obj = e.target;
            if (obj.scaleX < 0.2) {
                obj.scaleX = 0.2;
            }
            if (obj.scaleY < 0.2) {
                obj.scaleY = 0.2;
            }
        });

        // 使得图片可旋转
        canvas.on('object:rotating', (e) => {
            const obj = e.target;
            obj.setCoords();
        });

        return () => {
            canvas.dispose();
        };
    }, []);

    return (
        <div
            ref={canvasRef}
            style={{ width: '800px', height: '600px', border: '1px solid #ccc' }}
        ></div>
    );
};

export default FabricCanvas;
