import React, { useEffect, useRef } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);

    useEffect(() => {
        fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
            width: window.innerHeight * 0.6,
            height: window.innerHeight * 0.6,
        });

        // 添加事件监听器
        canvasRef.current.addEventListener('dragover', handleDragOver);
        canvasRef.current.addEventListener('drop', handleDrop);

        return () => {
            // 移除事件监听器
            canvasRef.current.removeEventListener('dragover', handleDragOver);
            canvasRef.current.removeEventListener('drop', handleDrop);
        };
    }, []); // 空依赖数组意味着这个effect只会在组件挂载和卸载时运行

    // 处理dragover事件
    const handleDragOver = (e) => {
        e.preventDefault();
    };

    // 处理drop事件
    const handleDrop = (e) => {
        e.preventDefault();

        // 获取拖拽的数据
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        // 如果是来自素材库的拖拽数据
        if (type === 'library') {
            // 创建fabric.Image对象
            fabric.Image.fromURL(url, (img) => {
                // 缩放图片到原来的四分之一大小
                img.scale(0.25);

                // 设置图片位置为画布的原点
                img.setCoords();

                // 添加图片到画布
                fabricCanvasRef.current.add(img);
                fabricCanvasRef.current.setActiveObject(img); // 设置为活动对象
                fabricCanvasRef.current.requestRenderAll(); // 重新渲染画布
            });
        }
    };

    // 处理删除对象
    const handleDeleteObject = () => {
        const activeObject = fabricCanvasRef.current.getActiveObject();
        if (activeObject) {
            fabricCanvasRef.current.remove(activeObject);
            fabricCanvasRef.current.requestRenderAll(); // 重新渲染画布
        }
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh' }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
            <button onClick={handleDeleteObject}>Delete Object</button>
        </div>
    );
};

export default FabricCanvas;
