import React, { useState,useEffect } from 'react';
import StepBar from '../../components/StepBar/StepBar';
import InputBox from '../../components/InputBox/InputBox';
import MaterialLibrary from '../../components/MaterialLibrary/MaterialLibrary';
import './GenerationImg.css';
import ShowImg from '../../components/ShowImg/ShowImg';

const GenerationImg = ({ currentStep, handleNextStep, handlePrevStep, selectedMaterials, onAddMaterial }) => {
  const [inputValue, setInputValue] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [showImages, setShowImages] = useSessionStorageState('showImages', false);
    const [generatedImages, setGeneratedImages] = useSessionStorageState('generatedImages', []);

// // д���汾
// const handleUpload = () => {
//   setIsUploading(true);
//   console.log('Uploading:', inputValue);
//
//   setTimeout(() => {
//     console.log('Uploaded:', inputValue);
//     setInputValue('');
//     setIsUploading(false);
//     setShowImages(true);
//   }, 2000);
// };

    function useSessionStorageState(key, defaultValue) {
        const [state, setState] = useState(() => {
            const storedValue = sessionStorage.getItem(key);
            return storedValue !== null ? JSON.parse(storedValue) : defaultValue;
        });

        useEffect(() => {
            sessionStorage.setItem(key, JSON.stringify(state));
        }, [key, state]);

        return [state, setState];
    }
const handleInputChange = (e) => {
  setInputValue(e.target.value);
};

    useEffect(() => {
        console.log('Show images updated to:', showImages);
        console.log('Generated images:', generatedImages);
    }, [showImages, generatedImages]);


const handleUpload = async () => {
  setIsUploading(true);
  console.log('Uploading:', inputValue);

  try {
      const response = await fetch('http://localhost:5000/generate-image', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          },
          body: JSON.stringify({ prompt: inputValue })
      });
      if (response.ok) {
          const imagesBase64 = await response.json();
          const imagesUrls = imagesBase64.map(imgBase64 => `data:image/png;base64,${imgBase64}`);
          setGeneratedImages(prevImages => [...imagesUrls, ...prevImages]); // ��ͼƬ����

          console.log(showImages)
      } else {
          console.error('Failed to generate image');
      }
  } catch (error) {
      console.error('Error:', error);
  }

  setInputValue('');
  setIsUploading(false);
};



  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleUpload();
    }
  };
  return (
    <div className="container">
    
      <div className="left-side">
        <StepBar currentStep={currentStep} handleNextStep={handleNextStep} handlePrevStep={handlePrevStep} />
        <div className="middle">
          {showImages && <ShowImg onAddToLibrary={onAddMaterial} images={generatedImages} />}
        </div>
        <div className="bottom">
          <InputBox value={inputValue} onChange={handleInputChange} onUpload={handleUpload} onKeyPress={handleKeyPress} isUploading={isUploading} />
        </div>
      </div>
      <div className="right-side">
        <MaterialLibrary materials={selectedMaterials} />
      </div>
    </div>
  );
};

export default GenerationImg;
