import React, { useState } from 'react';
import StepBar from '../../components/StepBar/StepBar';
import MaterialLibrary from '../../components/MaterialLibrary/MaterialLibrary';
import ShowImg from '../../components/ShowImg/ShowImg';
import './GenerationImg.css';
import LoadingAnimation from "../../components/loadingicon/loadingicon";

const GenerationImg = ({ currentStep, handleNextStep, handlePrevStep, selectedMaterials, onAddMaterial }) => {
    const [selectedImageIndex, setSelectedImageIndex] = useState(null); // 存储选中的图片索引
    const [prompt, setPrompt] = useState(''); // 存储用户输入的提示词
    const [generatedImages, setGeneratedImages] = useState([]); // 存储生成的图片
    const [isUploading, setIsUploading] = useState(false); // 上传状态

    // 选中图片时的回调函数
    const handleSelectImage = (index) => {
        setSelectedImageIndex(index); // 更新选中的图片索引
    };

    // 输入框内容变化时的回调函数
    const handleInputChange = (e) => {
        setPrompt(e.target.value); // 更新提示词
    };

    // 点击上传按钮时的回调函数
    const handleUpload = async () => {
        setIsUploading(true); // 设置上传状态为 true

        // 如果有选中的图片，发送请求生成图片
        if (selectedImageIndex !== null) {
            // 构建请求体
            const requestBody = {
                prompt: prompt, // 使用用户输入的提示词作为 prompt
                imageIndex: selectedImageIndex // 上传选中的图片索引
            };

            try {
                // 发送 POST 请求
                const response = await fetch('http://example.com/generate-image', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(requestBody)
                });

                if (response.ok) {
                    // 如果请求成功，获取生成的图片
                    const generatedImages = await response.json();
                    setGeneratedImages(generatedImages);
                } else {
                    console.error('Failed to generate image');
                }
            } catch (error) {
                console.error('Error:', error);
            }
        }

        setIsUploading(false); // 设置上传状态为 false
    };

    return (
        <div className="container">
            <div className="left-side">
                <StepBar currentStep={currentStep} handleNextStep={handleNextStep} handlePrevStep={handlePrevStep} />
                <div className="middle">
                    <div className="bgcolor">
                        <ShowImg images={selectedMaterials} onSelectImage={handleSelectImage} selectedImageIndex={selectedImageIndex} />
                    </div>
                </div>
                <div className="bottom">
                    <input
                        type="text"
                        value={prompt}
                        onChange={handleInputChange}
                        placeholder={selectedImageIndex !== null ? `选中的图片编号：${selectedImageIndex}` : "请输入内容作为生成垫图的 prompt"}
                    />
                    <button className="upload-button" onClick={handleUpload} disabled={isUploading}>
                        {isUploading ? <LoadingAnimation /> : '上传'}
                    </button>
                </div>
            </div>
            <div className="right-side">
                <MaterialLibrary materials={selectedMaterials} />
            </div>
        </div>
    );
};

export default GenerationImg;
