import React, { useRef, useState, useEffect } from 'react';
import { fabric } from 'fabric';

const Remake = ({ targets, setTargets }) => {
    const canvasRef = useRef(null);
    const [canvasSize, setCanvasSize] = useState({ width: 512, height: 512 });
    const [canvas, setCanvas] = useState(null);

    useEffect(() => {
        if (!canvas) {
            const newCanvas = new fabric.Canvas(canvasRef.current, {
                width: canvasSize.width,
                height: canvasSize.height,
                backgroundColor: 'white',
            });

            setCanvas(newCanvas);
        }

        return () => {
            if (canvas) {
                canvas.dispose(); // 清理fabric.Canvas对象
                setCanvas(null);
            }
        };
    }, [canvas, canvasSize]);

    useEffect(() => {
        if (canvas && targets.length > 0) {
            targets.forEach(target => {
                fabric.Image.fromURL(target.src, img => {
                    img.set({
                        left: target.x,
                        top: target.y,
                        width: target.width,
                        height: target.height,
                        selectable: true,
                        hasControls: true, // 显示缩放和旋转控件
                    });
                    canvas.add(img);
                });
            });
        }
    }, [canvas, targets]);

    useEffect(() => {
        if (canvas) {
            canvas.on('object:modified', e => {
                const modifiedObject = e.target;
                const updatedTargets = targets.map(target => {
                    if (target.id === modifiedObject.id) {
                        return {
                            ...target,
                            x: modifiedObject.left,
                            y: modifiedObject.top,
                            width: modifiedObject.width * modifiedObject.scaleX,
                            height: modifiedObject.height * modifiedObject.scaleY,
                            rotate: modifiedObject.angle,
                        };
                    }
                    return target;
                });
                setTargets(updatedTargets);
            });
        }
    }, [canvas, targets, setTargets]);

    const handleDrop = (event) => {
        event.preventDefault();
        const files = event.dataTransfer.files;
        for (let i = 0; i < files.length; i++) {
            if (files[i].type.startsWith('image/')) {
                const reader = new FileReader();
                reader.onload = (e) => {
                    const newTarget = {
                        src: e.target.result,
                        x: 0,
                        y: 0,
                        width: canvasSize.width / 3,
                        height: canvasSize.height / 3,
                        rotate: 0,
                    };
                    setTargets(prevTargets => [...prevTargets, newTarget]);
                };
                reader.readAsDataURL(files[i]);
            }
        }
    };

    const handleDragOver = (event) => {
        event.preventDefault();
    };

    return (
        <div
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            style={{
                height: '60vh',
                width: '80vh', // 设置宽度为80vh
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                overflow: 'hidden',
                position: 'relative',
            }}
        >
            <canvas
                ref={canvasRef}
                style={{ border: '1px solid black' }}
            />
        </div>
    );
};

export default Remake;
