import React, { useRef } from 'react';
import './ShowClothes.css';
import StepBar from '../../components/StepBar/StepBar';
import ClothBox from './ClothBox';
import ClothBox2 from "./ClothBox2";

const ShowClothes = ({ imgscale,setImgscale,setSpacing,spacing,currentStep, handleNextStep, handlePrevStep, sketchDataURL, patternDataURL,setConnectionType,connectionType }) => {

    const clothBoxRef = useRef(null);

    const handleSaveImage = () => {
        if (clothBoxRef.current) {
            clothBoxRef.current.saveCanvasAsImage();
        }
    };
    const increaseSpacing = () => {
        setSpacing(prevSpacing => prevSpacing + 100);
        // console.log(spacing);
    };

    // 减少间距
    const decreaseSpacing = () => {
        setSpacing(prevSpacing => prevSpacing - 100 >= 0 ? prevSpacing - 100 : 0);
        // console.log(spacing);
    };
    const bigscaleimg = () => {
        setImgscale(previmgscale => {
            // 如果当前值小于4，则可以增加
            if (previmgscale < 4) {
                return previmgscale + 1;
            } else {
                // 否则保持不变
                return previmgscale;
            }
        });
    };

    const smallscaleimg = () => {
        setImgscale(previmgscale => {
            // 如果当前值大于1，则可以减少
            if (previmgscale > 1) {
                return previmgscale - 1;
            } else {
                // 否则保持不变
                return previmgscale;
            }
        });
    };

    return (
        <div className="container containerColumn">
            <StepBar currentStep={currentStep} handleNextStep={handleNextStep} handlePrevStep={handlePrevStep} />
            <div className="middlecloth" >
                    <div className="clothbox"> <ClothBox2  imgscale={imgscale} setImgscale={setImgscale} setConnectionType={setConnectionType} connectionType={connectionType} spacing={spacing} ref={clothBoxRef} sketchDataURL={sketchDataURL} patternDataURL={patternDataURL} /></div>
                   <div className="clothbox"> <ClothBox  imgscale={imgscale} setImgscale={setImgscale} setConnectionType={setConnectionType} connectionType={connectionType} spacing={spacing} ref={clothBoxRef} sketchDataURL={sketchDataURL} patternDataURL={patternDataURL} /></div>
            </div>
            <div className="bottom">
                <div className="containerbox">
                    <span className="text-above-buttons"><span className="zoomimg">缩放纹样</span><span>大小缩放</span></span>
                    <div className="button-group">
                        <button className="bottom-buttonbig" onClick={increaseSpacing}>
                            <svg width="20" height="20" viewBox="0 0 20 20" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <path d="M2 10H18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                                <path d="M10 2L10 18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                            </svg>
                        </button>
                        <button className="bottom-buttonbig" onClick={decreaseSpacing}>
                            <svg width="20" height="4" viewBox="0 0 20 4" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <path d="M2 2H18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                            </svg>
                        </button>
                        <button className="bottom-buttonbig" onClick={bigscaleimg}>
                            <svg width="20" height="20" viewBox="0 0 24 24" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <circle cx="12" cy="12" r="10" stroke="white" stroke-width="2"/>
                                <line x1="12" y1="8" x2="12" y2="16" stroke="white" stroke-width="2"
                                      stroke-linecap="round"/>
                                <line x1="8" y1="12" x2="16" y2="12" stroke="white" stroke-width="2"
                                      stroke-linecap="round"/>
                            </svg>
                        </button>
                        <button className="bottom-buttonbig" onClick={smallscaleimg}>
                            <svg width="20" height="20" viewBox="0 0 24 24" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <circle cx="12" cy="12" r="10" stroke="white" stroke-width="2"/>
                                <line x1="8" y1="12" x2="16" y2="12" stroke="white" stroke-width="2"
                                      stroke-linecap="round"/>
                            </svg>
                        </button>
                    </div>
                    <button className="bottom-button" onClick={handleSaveImage}>输出印花文件</button>
                </div>

            </div>
        </div>
    );
};

export default ShowClothes;
