import React, { useEffect, useRef, useState } from 'react';
import { fabric } from 'fabric';
import myimg from '../../images/show1.png';

const FabricCanvas = () => {
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);

    useEffect(() => {
        fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
            width: window.innerHeight * 0.6,
            height: window.innerHeight * 0.6,
            selection: true,
            preserveObjectStacking: true,
            interactive: true,
        });

        // 添加默认图片
        fabric.Image.fromURL(myimg, (img) => {
            img.set({
                left: 50,
                top: 50,
                hasControls: true,
                hasBorders: true,
                cornerColor: 'black',
                cornerSize: 10,
                transparentCorners: false,
                selectable: true,
                originX: 'center',
                originY: 'center',
                lockRotation: false
            });
            fabricCanvasRef.current.add(img);
            fabricCanvasRef.current.renderAll();
        });

        // 添加一个可交互的长方形
        const rect = new fabric.Rect({
            left: 150,
            top: 150,
            width: 100,
            height: 100,
            fill: 'red',
            selectable: true,
            hasControls: true,
            hasBorders: true,
            cornerColor: 'black',
            cornerSize: 10,
            originX: 'center',
            originY: 'center',
            lockRotation: false
        });
        fabricCanvasRef.current.add(rect);
        fabricCanvasRef.current.renderAll();
    }, []);

    const handleDrop = (e) => {
        e.preventDefault();
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);
        fabric.Image.fromURL(url, (img) => {
            img.set({
                left: e.clientX - canvasRef.current.getBoundingClientRect().left,
                top: e.clientY - canvasRef.current.getBoundingClientRect().top,
                hasControls: true,
                hasBorders: true,
                cornerColor: 'black',
                cornerSize: 10,
                transparentCorners: false,
                selectable: true,
                originX: 'center',
                originY: 'center',
                lockRotation: false,
            });
            fabricCanvasRef.current.add(img);
            fabricCanvasRef.current.renderAll();
        });
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    return (
        <div
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh', position: 'relative' }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
        </div>
    );
};

export default FabricCanvas;
