import React, { useState, useEffect } from 'react';
import './loadingicon.css';

function LoadingAnimation() {
    const [iconClass, setIconClass] = useState('icon-1-2s'); // ��ʼΪ��1-2���ͼ��

    useEffect(() => {
        let timeoutId;
        const interval = setInterval(() => {
            setIconClass('icon-3s'); // 应用快速旋转动画和更新的SVG图标

            timeoutId = setTimeout(() => {
                setIconClass(''); // 回到没有旋转的初始状态
            }, 1000); // 1秒后（第3秒结束时）
        }, 3000); // 每3秒循环一次

        return () => {
            clearInterval(interval);
            clearTimeout(timeoutId);
        };
    }, []);
    return (
        // <div className="loading-animation-container">
            <div className="backboard">
                <div className="loading-container">
                    <div className="loader-icon"></div>
                    <svg className={`eye-icon ${iconClass}`} width="68" height="23" viewBox="0 0 68 23" fill="none"
                         xmlns="http://www.w3.org/2000/svg">
                        <svg width="68" height="23" viewBox="0 0 68 23" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path
                                d="M34.5 2.5625C21.3 2.5625 8.33333 9.24409 3 11.6968C9 14.9108 22.9 19.5625 34.5 19.5625C46.1 19.5625 60 14.3187 65.5 11.6968C60.6667 8.65205 47.7 2.5625 34.5 2.5625Z"
                                fill="#FEDC00" stroke="#373236" stroke-width="5" stroke-linejoin="round"/>
                            <circle cx="34.4375" cy="11" r="11" fill="#373236"/>
                        </svg>
                    </svg>
                    <div className="icontext">图片生成中......</div>
                </div>
            </div>
        // </div>


    );
}

export default LoadingAnimation;
