import React, { useEffect, useRef, useState } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = ({ setDataUrl }) => { // 接收父组件传递的状态变量
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);
    const [dataUrl, setDataUrlLocal] = useState(""); // 创建本地状态变量用于存储图案

    useEffect(() => {
        // Initialize canvas
        const canvas = canvasRef.current;
        if (canvas) {
            canvas.addEventListener('dragover', handleDragOver);
            canvas.addEventListener('drop', handleDrop);
        }

        return () => {
            // Clean canvas event listeners
            const canvas = canvasRef.current;
            if (canvas) {
                canvas.removeEventListener('dragover', handleDragOver);
                canvas.removeEventListener('drop', handleDrop);
            }
        };
    }, []);

    useEffect(() => {
        // Initialize fabric canvas
        if (!fabricCanvasRef.current) {
            fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
                width: window.innerHeight * 0.6,
                height: window.innerHeight * 0.6,
                selection: true,
                preserveObjectStacking: true,
                interactive: true,
            });
        }
    }, []);

    // Handle dragover event
    const handleDragOver = (e) => {
        e.preventDefault();
    };

    // Handle drop event
    const handleDrop = (e) => {
        e.preventDefault();
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        if (type === 'library') {
            fabric.Image.fromURL(
                url,
                (img) => {
                    img.set({
                        selectable: true,
                        scalable: true,
                        hasControls: true,
                    });
                    img.scale(0.25);
                    img.setCoords();

                    // Add delete button to each object's bounding box
                    const deleteButton = new fabric.Text('×', {
                        fontSize: 20,
                        fontWeight: 'bold',
                        fontFamily: 'Arial',
                        fill: 'red',
                        originX: 'right',
                        originY: 'top',
                        visible: false,
                    });

                    img.on('selected', () => {
                        deleteButton.set({
                            visible: true,
                        });
                    });

                    img.on('deselected', () => {
                        deleteButton.set({
                            visible: false,
                        });
                    });

                    deleteButton.on('mousedown', () => {
                        fabricCanvasRef.current.remove(img);
                        fabricCanvasRef.current.remove(deleteButton);
                        fabricCanvasRef.current.renderAll();

                    });
                    saveCanvasToDataUrl(); // 调用函数保存画布到 Data URL
                    fabricCanvasRef.current.add(img);
                    fabricCanvasRef.current.add(deleteButton);

                    saveCanvasToDataUrl(); // 添加对象后保存画布到 Data URL
                },
                { crossOrigin: 'anonymous' }
            );
        }
    };

    // 保存画布到 Data URL
    const saveCanvasToDataUrl = () => {
        console.log(123)
        if (fabricCanvasRef.current) {
            const dataURL = fabricCanvasRef.current.toDataURL({
                format: 'png',
                multiplier: 1,
                quality: 1
            });
            setDataUrlLocal(dataURL); // 存入本地状态变量
            setDataUrl(dataURL); // 传递给父组件
            console.log(dataURL);
        }
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh', position: 'relative', backgroundColor: 'white' }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
        </div>
    );
};

export default FabricCanvas;
