// Remake.js
import React, { useRef, useEffect } from 'react';
import { fabric } from 'fabric';

const Remake = ({ targets, setTargets }) => {
    const canvasRef = useRef(null);

    useEffect(() => {
        let canvas;

        const initCanvas = () => {
            canvas = new fabric.Canvas(canvasRef.current, {
                width: 512,
                height: 512,
                backgroundColor: 'white',
            });

            // 设置画布的缩放和拖拽功能
            canvas.setZoom(1);
            canvas.setViewportTransform([1, 0, 0, 1, 0, 0]);
            canvas.selection = true;

            // 添加拖放事件监听器
            canvasRef.current.addEventListener('drop', handleDrop);
            canvasRef.current.addEventListener('dragover', handleDragOver);
        };

        const handleDrop = (event) => {
            event.preventDefault();
            const imageUrl = event.dataTransfer.getData('text/plain');
            const imgElement = new Image();
            imgElement.src = imageUrl;
            imgElement.onload = () => {
                const imgInstance = new fabric.Image(imgElement, {
                    left: 0,
                    top: 0,
                    angle: 0,
                    scaleX: 0.1,
                    scaleY: 0.1,
                });
                canvas.add(imgInstance);
                setTargets((prevTargets) => [...prevTargets, imgInstance]);
            };
        };

        const handleDragOver = (event) => {
            event.preventDefault();
        };

        initCanvas();

        return () => {
            if (canvas) {
                canvas.dispose();
            }
            canvasRef.current.removeEventListener('drop', handleDrop);
            canvasRef.current.removeEventListener('dragover', handleDragOver);
        };
    }, [setTargets]);

    return (
        <div className="sketch-box">
            <canvas ref={canvasRef} />
        </div>
    );
};

export default Remake;
