import React, { useEffect, useRef, useState } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);
    const [selectedObject, setSelectedObject] = useState(null);
    const [isDown, setIsDown] = useState(false);
    const [mouseOrigin, setMouseOrigin] = useState({ x: 0, y: 0 });

    useEffect(() => {
        fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
            width: window.innerHeight * 0.6,
            height: window.innerHeight * 0.6,
            selection: false, // 禁用默认选择
        });

        // 添加事件监听器
        const canvas = fabricCanvasRef.current;
        if (canvas) {
            canvas.on('mouse:down', handleMouseDown);
            canvas.on('mouse:move', handleMouseMove);
            canvas.on('mouse:up', handleMouseUp);
        }

        return () => {
            // 移除事件监听器
            const canvas = fabricCanvasRef.current;
            if (canvas) {
                canvas.off('mouse:down', handleMouseDown);
                canvas.off('mouse:move', handleMouseMove);
                canvas.off('mouse:up', handleMouseUp);
            }
        };
    }, []); // 空依赖数组意味着这个effect只会在组件挂载和卸载时运行

    // 处理鼠标按下事件
    const handleMouseDown = (e) => {
        const canvas = fabricCanvasRef.current;
        const pointer = canvas.getPointer(e.e);

        // 获取点击位置上的对象
        const obj = canvas.findTarget(e.e);
        if (obj) {
            setSelectedObject(obj);
            setMouseOrigin({ x: pointer.x - obj.left, y: pointer.y - obj.top });
        } else {
            setSelectedObject(null);
        }

        setIsDown(true);
    };

    // 处理鼠标移动事件
    const handleMouseMove = (e) => {
        if (!isDown || !selectedObject) return;

        const canvas = fabricCanvasRef.current;
        const pointer = canvas.getPointer(e.e);
        selectedObject.set({
            left: pointer.x - mouseOrigin.x,
            top: pointer.y - mouseOrigin.y,
        });
        canvas.requestRenderAll();
    };

    // 处理鼠标抬起事件
    const handleMouseUp = () => {
        setIsDown(false);
    };

    // 处理删除对象
    const handleDeleteObject = () => {
        if (!selectedObject) return;
        fabricCanvasRef.current.remove(selectedObject);
        fabricCanvasRef.current.requestRenderAll();
        setSelectedObject(null);
    };

    // 处理dragover事件
    const handleDragOver = (e) => {
        e.preventDefault();
    };

    // 处理drop事件
    const handleDrop = (e) => {
        e.preventDefault();
        // 获取拖拽的数据
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        // 如果是来自素材库的拖拽数据
        if (type === 'library') {
            // 创建fabric.Image对象
            fabric.Image.fromURL(
                url,
                (img) => {
                    // 设置图片为可选择和可缩放
                    img.set({
                        selectable: true,
                        scalable: true,
                        hasControls: true, // 启用控制点
                        hasBorders: true, // 启用边框
                        hasRotatingPoint: true, // 启用旋转点
                        left: fabricCanvasRef.current.width / 2, // 将图像置于画布中心
                        top: fabricCanvasRef.current.height / 2,
                        originX: 'center', // 设置旋转和缩放的中心点
                        originY: 'center'
                    });

                    // 缩放图片到原来的四分之一大小
                    img.scale(0.25);

                    // 添加图片到画布
                    fabricCanvasRef.current.add(img);
                    setSelectedObject(img);
                    fabricCanvasRef.current.renderAll();
                },
                { crossOrigin: 'anonymous' }
            ); // 允许跨域请求
        }
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh', position: 'relative' }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
            <button onClick={handleDeleteObject} style={{ position: 'absolute', top: '10px', right: '10px' }}>
                删除对象
            </button>
        </div>
    );
};

export default FabricCanvas;
