import React, { useEffect, useRef, useState } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = ({ setDataUrl }) => {
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);
    const [dataUrl, setDataUrlLocal] = useState("");

    useEffect(() => {
        const canvas = canvasRef.current;
        if (canvas) {
            canvas.addEventListener('dragover', handleDragOver);
            canvas.addEventListener('drop', handleDrop);
        }

        return () => {
            const canvas = canvasRef.current;
            if (canvas) {
                canvas.removeEventListener('dragover', handleDragOver);
                canvas.removeEventListener('drop', handleDrop);
            }
        };
    }, []);

    useEffect(() => {
        if (!fabricCanvasRef.current) {
            fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
                width: window.innerHeight * 0.6,
                height: window.innerHeight * 0.6,
                selection: true,
                preserveObjectStacking: true,
                interactive: true,
            });
            fabricCanvasRef.current.on('object:selected', addDeleteButton);
            fabricCanvasRef.current.on('selection:cleared', removeDeleteButton);
        }
    }, []);

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleDrop = (e) => {
        e.preventDefault();
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        if (type === 'library') {
            fabric.Image.fromURL(
                url,
                (img) => {
                    img.set({
                        selectable: true,
                        scalable: true,
                        hasControls: true,
                    });
                    img.scale(0.25);
                    img.setCoords();

                    saveCanvasToDataUrl();
                    fabricCanvasRef.current.add(img);
                },
                { crossOrigin: 'anonymous' }
            );
        }
    };

    const saveCanvasToDataUrl = () => {
        if (fabricCanvasRef.current) {
            const dataURL = fabricCanvasRef.current.toDataURL({
                format: 'png',
                multiplier: 1,
                quality: 1
            });
            setDataUrlLocal(dataURL);
            setDataUrl(dataURL);
            console.log(dataURL);
        }
    };

    const addDeleteButton = (e) => {
        const selectedObject = e.target;
        const deleteButton = new fabric.Text('x', {
            fontFamily: 'Arial',
            fontSize: 20,
            fill: 'red',
            originX: 'right',
            originY: 'top',
            selectable: false,
            evented: false,
        });
        deleteButton.set({
            top: selectedObject.top,
            left: selectedObject.left + selectedObject.width,
        });
        deleteButton.on('mousedown', () => {
            fabricCanvasRef.current.remove(selectedObject);
            fabricCanvasRef.current.remove(deleteButton);
            saveCanvasToDataUrl();
        });
        selectedObject.deleteButton = deleteButton; // Store delete button reference in selected object
        fabricCanvasRef.current.add(deleteButton);
        fabricCanvasRef.current.renderAll();
    };

    const removeDeleteButton = () => {
        fabricCanvasRef.current.getObjects().forEach(obj => {
            if (obj.deleteButton) {
                fabricCanvasRef.current.remove(obj.deleteButton);
                delete obj.deleteButton;
            }
        });
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh', position: 'relative', backgroundColor: 'white' }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
        </div>
    );
};

export default FabricCanvas;
