import React, { useEffect, useRef } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = new fabric.Canvas(canvasRef.current, {
            width: 512,
            height: 512,
        });

        // 在此处可以添加其他初始化操作，例如添加图形等

        return () => {
            canvas.dispose(); // 清理fabric.Canvas对象
        };
    }, []);

    return (
        <div
            style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                width: '60vh', // 或者其他你希望的宽度
                height: '60vh', // 或者其他你希望的高度
            }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ border: '1px solid black' }}
            ></canvas>
        </div>
    );
};

export default FabricCanvas;
