import React, { useEffect, useRef } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasContainerRef = useRef(null);
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);

    useEffect(() => {
        fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
            width: window.innerHeight * 0.6,
            height: window.innerHeight * 0.6,
        });

        // 在此处可以添加其他初始化操作，例如添加图形等

        return () => {
            fabricCanvasRef.current.dispose(); // 清理fabric.Canvas对象
        };
    }, []);

    return (
        <div
            ref={canvasContainerRef}
            style={{
                position: 'relative', // 设置父元素的position为relative
                width: '60vh',
                height: '60vh',
            }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ border: '1px solid black', maxWidth: '100%', maxHeight: '100%',background:'white' }}
            ></canvas>
        </div>
    );
};

export default FabricCanvas;
