import React, { useRef, useState, useEffect } from 'react';

const Remake = ({ targets, setTargets }) => {
    const canvasRef = useRef(null);
    const [canvasSize, setCanvasSize] = useState({ width: 512, height: 512 });
    const [context, setContext] = useState(null);
    const [deleteButtonVisible, setDeleteButtonVisible] = useState(false); // 控制删除按钮的可见性
    const [deleteButtonPosition, setDeleteButtonPosition] = useState({ x: 0, y: 0 }); // 删除按钮的位置

    useEffect(() => {
        if (canvasRef.current) {
            const ctx = canvasRef.current.getContext('2d');
            setContext(ctx);
        }
    }, [canvasRef.current]);

    useEffect(() => {
        if (context && targets.length > 0) {
            context.clearRect(0, 0, canvasSize.width, canvasSize.height);
            targets.forEach(target => {
                const img = new Image();
                img.src = target.src;
                img.onload = () => {
                    context.drawImage(img, target.x, target.y, target.width, target.height);
                };
            });
        }
    }, [context, targets, canvasSize]);

    const handleDrop = (event) => {
        event.preventDefault();
        const files = event.dataTransfer.files;
        for (let i = 0; i < files.length; i++) {
            if (files[i].type.startsWith('image/')) {
                const reader = new FileReader();
                reader.onload = (e) => {
                    setTargets(prevTargets => [...prevTargets, {
                        src: e.target.result,
                        x: 0, // 初始位置
                        y: 0,
                        width: canvasSize.width / 3, // 初始大小
                        height: canvasSize.height / 3,
                    }]);
                };
                reader.readAsDataURL(files[i]);
            }
        }
    };

    const handleDragOver = (event) => {
        event.preventDefault();
    };

    const handleCanvasClick = (event) => {
        const rect = canvasRef.current.getBoundingClientRect();
        const x = event.clientX - rect.left;
        const y = event.clientY - rect.top;
        const clickedTarget = targets.find(target => {
            return x >= target.x && x <= (target.x + target.width) &&
                y >= target.y && y <= (target.y + target.height);
        });
        if (clickedTarget) {
            setDeleteButtonVisible(true);
            setDeleteButtonPosition({ x: clickedTarget.x + clickedTarget.width, y: clickedTarget.y }); // 将删除按钮放置在图片的右上角
        } else {
            setDeleteButtonVisible(false);
        }
    };

    const handleDeleteButtonClick = () => {
        // 删除点击的图片对象
        const updatedTargets = targets.filter(target => {
            const deleteButtonX = deleteButtonPosition.x;
            const deleteButtonY = deleteButtonPosition.y;
            return !(deleteButtonX >= target.x && deleteButtonX <= (target.x + target.width) &&
                deleteButtonY >= target.y && deleteButtonY <= (target.y + target.height));
        });
        setTargets(updatedTargets);
        setDeleteButtonVisible(false);
    };

    return (
        <div
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            style={{
                height: '60vh',
                width: '80vh', // 设置宽度为80vh
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                overflow: 'hidden',
                position: 'relative',
            }}
        >
            <canvas
                ref={canvasRef}
                width={canvasSize.width}
                height={canvasSize.height}
                style={{ border: '1px solid black' }}
                onClick={handleCanvasClick}
            />
            {deleteButtonVisible && (
                <div
                    style={{
                        position: 'absolute',
                        left: deleteButtonPosition.x,
                        top: deleteButtonPosition.y,
                        width: 40,
                        height: 40,
                        backgroundColor: 'red',
                        color: 'white',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        cursor: 'pointer',
                    }}
                    onClick={handleDeleteButtonClick}
                >
                    X
                </div>
            )}
        </div>
    );
};

export default Remake;
