import React from 'react';
import './MaterialLibrary.css'; // 确保你有一个CSS文件来定义样式

function MaterialLibrary({ materials }) { // 通过props接收materials
  // 不再需要内部状态images，因为我们将使用从props传递过来的materials数组
 // console.log(materials)
 // 在 MaterialLibrary 组件中
// MaterialLibrary 组件内
function handleDragStart(event, imageUrl) {
  // 将图片 URL 作为拖拽数据设置
  const dragData = JSON.stringify({ type: 'library', url: imageUrl });
  event.dataTransfer.setData('application/json', dragData);
}


  return (
    <div>
      <div className="title-bar">
        <span>★ 素材库</span>
      </div>
      <div className="image-container">
        {materials && materials.map((image, index) => ( // 使用传入的materials渲染图片
         <img
         key={index}
         src={image}
         draggable="true"
         onDragStart={(e) => handleDragStart(e, image)}
         alt={`Material ${index}`}
         className="material-image"
       />
       
        ))}
      </div>
    </div>
  );
}

export default MaterialLibrary;
