import React, {useRef, useState} from 'react';
import './ShowClothes.css';
import StepBar from '../../components/StepBar/StepBar';
import ClothBox from './ClothBox';
import ClothBox2 from "./ClothBox2";

const ShowClothes = ({ selectedColor,imgscale,setImgscale,setSpacing,spacing,currentStep, handleNextStep, handlePrevStep, sketchDataURL, patternDataURL,setConnectionType,connectionType }) => {

    const clothBoxRef = useRef(null);


    const handleSaveImage = () => {
        if (clothBoxRef.current) {
            clothBoxRef.current.saveCanvasAsImage();
        }
    };
    const increaseSpacing = () => {
        setSpacing(prevSpacing => prevSpacing + 100);
        // console.log(spacing);
    };

    // 减少间距
    const decreaseSpacing = () => {
        setSpacing(prevSpacing => prevSpacing - 100 >= 0 ? prevSpacing - 100 : 0);
        // console.log(spacing);
    };
    const bigscaleimg = () => {
        setImgscale(previmgscale => {
            // 如果当前值小于4，则可以增加
            if (previmgscale < 2) {
                return previmgscale + 0.25;
            } else {
                // 否则保持不变
                return previmgscale;
            }
        });
    };

    const smallscaleimg = () => {
        setImgscale(previmgscale => {
            // 如果当前值大于1，则可以减少
            if (previmgscale > 1) {
                return previmgscale - 0.25;
            } else {
                // 否则保持不变
                return previmgscale;
            }
        });
    };

    return (
        <div className="container containerColumn">
            <StepBar currentStep={currentStep} handleNextStep={handleNextStep} handlePrevStep={handlePrevStep} />
            <div className="bgcolor">
            <div className="middlecloth" >
                    <div className="clothbox"> <ClothBox2 selectedColor={selectedColor} imgscale={imgscale} setImgscale={setImgscale} setConnectionType={setConnectionType} connectionType={connectionType} spacing={spacing} ref={clothBoxRef} sketchDataURL={sketchDataURL} patternDataURL={patternDataURL} /></div>
                   <div className="clothbox"> <ClothBox  selectedColor={selectedColor} imgscale={imgscale} setImgscale={setImgscale} setConnectionType={setConnectionType} connectionType={connectionType} spacing={spacing} ref={clothBoxRef} sketchDataURL={sketchDataURL} patternDataURL={patternDataURL} /></div>
            </div>
            </div>
            <div className="bottom">
                <div className="containerbox">
                    <span className="text-above-buttons"><span className="zoomimg">Spacing</span><span className="sizetext">Size</span></span>
                    <div className="button-group">
                        <button className="bottom-buttonbig" onClick={increaseSpacing}>
                            <svg width="20" height="20" viewBox="0 0 20 20" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <path d="M2 10H18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                                <path d="M10 2L10 18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                            </svg>
                        </button>
                        <button className="bottom-buttonbig" onClick={decreaseSpacing}>
                            <svg width="20" height="4" viewBox="0 0 20 4" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <path d="M2 2H18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                            </svg>
                        </button>
                        <button className="bottom-buttonbig" onClick={bigscaleimg}>
                            <svg width="20" height="20" viewBox="0 0 20 20" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <path d="M2 10H18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                                <path d="M10 2L10 18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                            </svg>
                        </button>
                        <button className="bottom-buttonbig" onClick={smallscaleimg}>
                            <svg width="20" height="4" viewBox="0 0 20 4" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <path d="M2 2H18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                            </svg>
                        </button>
                    </div>
                    <button className="bottom-button" onClick={handleSaveImage}>Pattern file output</button>
                </div>

            </div>
        </div>
    );
};

export default ShowClothes;
