import React, { useRef,useState } from 'react';
import './ShowClothes.css';
import StepBar from '../../components/StepBar/StepBar';
import ClothBox from './ClothBox';
import ContinuousPatterns from "../../components/ContinuousPatterns/ContinuousPatterns";
import {findAllByDisplayValue} from "@testing-library/react";
const ShowClothes = ({ setSpacing,spacing,currentStep, handleNextStep, handlePrevStep, sketchDataURL, patternDataURL }) => {

    const clothBoxRef = useRef(null);

    const handleSaveImage = () => {
        if (clothBoxRef.current) {
            clothBoxRef.current.saveCanvasAsImage();
        }
    };
    const increaseSpacing = () => {
        setSpacing(prevSpacing => prevSpacing + 10);
        // console.log(spacing);
    };

    // 减少间距
    const decreaseSpacing = () => {
        setSpacing(prevSpacing => prevSpacing - 10 >= 0 ? prevSpacing - 10 : 0);
        // console.log(spacing);
    };

    return (
        <div className="container containerColumn">
            <StepBar currentStep={currentStep} handleNextStep={handleNextStep} handlePrevStep={handlePrevStep} />
            <div className="middlecloth" >
                    <div className="clothbox"> <ClothBox spacing={spacing} ref={clothBoxRef} sketchDataURL={sketchDataURL} patternDataURL={patternDataURL} /></div>
                   <div className="clothbox"> <ClothBox   spacing={spacing} ref={clothBoxRef} sketchDataURL={sketchDataURL} patternDataURL={patternDataURL} /></div>
            </div>
            <div className="bottom">
                <div className="box">
                <div>
                    缩放纹样
                </div>
                    <div className="containerbutton">
                        <button className="bottom-buttonbig"
                                onClick={increaseSpacing}>+
                        </button>

                        <button className="bottom-buttonbig" onClick={decreaseSpacing}>-
                        </button>
                        <button className="bottom-button" onClick={handleSaveImage}>输出印花文件</button>
                    </div>

                </div>
            </div>
        </div>
    );
};

export default ShowClothes;
