import React, { useEffect, useRef, useState } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);
    const [isDown, setIsDown] = useState(false);
    const [lastPosX, setLastPosX] = useState(null);
    const [lastPosY, setLastPosY] = useState(null);

    useEffect(() => {
        fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
            width: window.innerHeight * 0.6,
            height: window.innerHeight * 0.6,
            selection: false, // 开启默认选择框
            preserveObjectStacking: true, // 保持对象堆叠顺序
            interactive: true, // 启用对象交互性
        });


        const canvas = fabricCanvasRef.current;
        if (canvas) {
            console.log('afsfasfs')
            canvas.on('mouse:down', handleMouseDown);
            canvas.on('mouse:move', handleMouseMove);
            canvas.on('mouse:up', handleMouseUp);
        }

        return () => {
            // 移除事件监听器
            const canvas = fabricCanvasRef.current;
            if (canvas) {
                canvas.off('mouse:down', handleMouseDown);
                canvas.off('mouse:move', handleMouseMove);
                canvas.off('mouse:up', handleMouseUp);
            }
        };
    }, []); // 空依赖数组意味着这个effect只会在组件挂载和卸载时运行


    // 处理drop事件
    const handleDrop = (e) => {
        e.preventDefault(); // 阻止默认行为

        // 获取拖拽的数据
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        // 如果是来自素材库的拖拽数据
        if (type === 'library') {
            // 创建fabric.Image对象
            fabric.Image.fromURL(
                url,
                (img) => {
                    img.set({
                        left: 100,
                        top: 100,
                        width: 200,
                        height: 150,
                        hasControls: true,
                        hasBorders: true,

                        cornerSize: 10,
                        transparentCorners: false,
                        selectable: true,
                        originX: 'center',
                        originY: 'center',
                        lockRotation: false
                    });

                    // 添加图片到画布
                    fabricCanvasRef.current.add(img);
                    fabricCanvasRef.current.setActiveObject(img); // 设置为活动对象
                    fabricCanvasRef.current.renderAll();
                },
                { crossOrigin: 'anonymous' }
            );
        }
    };


    // 处理鼠标按下事件
    const handleMouseDown = (e) => {
        const pointer = fabricCanvasRef.current.getPointer(e.e);
        const obj = fabricCanvasRef.current.findTarget(e.e);

        if (obj) {
            setIsDown(true);
            setLastPosX(pointer.x - obj.left);
            setLastPosY(pointer.y - obj.top);
        }
    };

    // 处理鼠标移动事件
    const handleMouseMove = (e) => {
        if (!isDown) return;
        const pointer = fabricCanvasRef.current.getPointer(e.e);
        const obj = fabricCanvasRef.current.findTarget(e.e);

        if (obj) {
            obj.set({
                left: pointer.x - lastPosX,
                top: pointer.y - lastPosY
            });
            fabricCanvasRef.current.renderAll();
        }
    };

    // 处理鼠标抬起事件
    const handleMouseUp = () => {
        setIsDown(false);
    };
    // 处理dragover事件
    const handleDragOver = (e) => {
        e.preventDefault();
    };


    return (
        <div
            onDrop={handleDrop}
            onDragOver={(e) => e.preventDefault()} // 阻止默认行为
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh', position: 'relative' }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
        </div>
    );

};

export default FabricCanvas;

