import React, { useEffect, useRef } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);

    useEffect(() => {
        fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
            width: window.innerHeight * 0.6,
            height: window.innerHeight * 0.6,
            selection: true,
            preserveObjectStacking: true,
        });

        // Fabric.js画布的事件监听器
        fabricCanvasRef.current.on('object:selected', handleObjectSelected);
        fabricCanvasRef.current.on('selection:cleared', handleSelectionCleared);

        // 拖放事件监听器
        canvasRef.current.addEventListener('dragover', handleDragOver);
        canvasRef.current.addEventListener('drop', handleDrop);

        return () => {
            fabricCanvasRef.current.off('object:selected', handleObjectSelected);
            fabricCanvasRef.current.off('selection:cleared', handleSelectionCleared);
            canvasRef.current.removeEventListener('dragover', handleDragOver);
            canvasRef.current.removeEventListener('drop', handleDrop);
        };
    }, []);

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleDrop = (e) => {
        e.preventDefault();

        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        if (type === 'library') {
            fabric.Image.fromURL(
                url,
                (img) => {
                    img.set({
                        selectable: true,
                        scaleX: 0.25,
                        scaleY: 0.25,
                        left: e.layerX,
                        top: e.layerY,
                    });
                    fabricCanvasRef.current.add(img);
                    fabricCanvasRef.current.setActiveObject(img);
                    fabricCanvasRef.current.requestRenderAll();
                },
                { crossOrigin: 'anonymous' }
            );
        }
    };

    const handleDeleteObject = () => {
        const activeObject = fabricCanvasRef.current.getActiveObject();
        if (activeObject) {
            fabricCanvasRef.current.remove(activeObject);
            fabricCanvasRef.current.discardActiveObject();
            fabricCanvasRef.current.requestRenderAll();
        }
    };

    const handleObjectSelected = () => {
        // 针对选中对象的额外处理，如果需要的话
    };

    const handleSelectionCleared = () => {
        // 针对清除选择的额外处理，如果需要的话
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh', position: 'relative' }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
            <button onClick={handleDeleteObject} style={{ position: 'absolute', top: '10px', right: '10px' }}>删除对象</button>
        </div>
    );
};

export default FabricCanvas;
