import React, { useEffect, useRef } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = new fabric.Canvas(canvasRef.current, {
            width: 512,
            height: 512,
        });

        // 在此处可以添加其他初始化操作，例如添加图形等

        return () => {
            canvas.dispose(); // 清理fabric.Canvas对象
        };
    }, []);

    return (
        <div
            style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                width: '100%', // 确保父元素占据全部可用空间
                height: '100%', // 确保父元素占据全部可用空间
            }}
        >
            <canvas
                ref={canvasRef}
                style={{
                    border: '1px solid black',
                    maxWidth: '60vh', // 最大宽度为父元素高度的60%
                    maxHeight: '60vh', // 最大高度为父元素高度的60%
                    width: '100%', // 宽度占据全部可用空间
                    height: '100%', // 高度占据全部可用空间
                }}
            ></canvas>
        </div>
    );
};

export default FabricCanvas;
