import React, { useEffect, useRef } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = new fabric.Canvas(canvasRef.current, {
            width: `${window.innerHeight * 0.6}px`, // 使用window.innerHeight获取视口高度
            height: `${window.innerHeight * 0.6}px`,
            position:"relative",
        });


        // 在此处可以添加其他初始化操作，例如添加图形等

        return () => {
            canvas.dispose(); // 清理fabric.Canvas对象
        };
    }, []);

    return (
        <canvas
            ref={canvasRef}
            width={512}
            height={512}
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh',position:"relative" }}
        ></canvas>
    );
};

export default FabricCanvas;
