import React, { useEffect, useRef, useState } from 'react';
import './ShowImg.css';

const ShowImg = ({ onAddToLibrary, images }) => {
  // 使用 sessionStorage 恢复 selectedImg 状态
  const [selectedImg, setSelectedImg] = useState(() => {
    const savedSelectedImg = sessionStorage.getItem('selectedImg');
    return savedSelectedImg ? JSON.parse(savedSelectedImg) : null;
  });

  // 使用 sessionStorage 恢复 isZoomed 状态
  const [isZoomed, setIsZoomed] = useState(() => {
    const savedIsZoomed = sessionStorage.getItem('isZoomed');
    return savedIsZoomed ? JSON.parse(savedIsZoomed) : false;
  });

  const [buttonStyle, setButtonStyle] = useState({});
  const [isButtonsVisible, setIsButtonsVisible] = useState(true);
  const galleryRef = useRef(null);

  // 使用 sessionStorage 恢复 addedImages 状态
  const [addedImages, setAddedImages] = useState(() => {
    const savedAddedImages = sessionStorage.getItem('addedImages');
    return savedAddedImages ? JSON.parse(savedAddedImages) : [];
  });

  useEffect(() => {
    sessionStorage.setItem('selectedImg', JSON.stringify(selectedImg));
    sessionStorage.setItem('isZoomed', JSON.stringify(isZoomed));
    sessionStorage.setItem('addedImages', JSON.stringify(addedImages));
  }, [selectedImg, isZoomed, addedImages]);

  const handleImageClick = (index, event) => {
    setSelectedImg(index);
    setIsZoomed(false);
    setIsButtonsVisible(true);
    const rect = event.target.getBoundingClientRect();
    const isLeftSide = index % 2 === 0;
    let buttonLeftStyle = isLeftSide ? rect.left - 100 + 'px' : rect.right + 25 + 'px';

    setButtonStyle({
      position: 'absolute',
      top: `${rect.top}px`,
      left: buttonLeftStyle,
    });
  };

  const handleAddClick = () => {
    if (selectedImg !== null) {
      const imageUrl = images[selectedImg];
      if (!addedImages.includes(imageUrl)) {
        onAddToLibrary(imageUrl);
        setAddedImages(prev => [...prev, imageUrl]);
      } else {
        alert("This image has already been added.");
      }
    }
  };

  const handleZoomImage = () => {
    if (selectedImg !== null) {
      setIsZoomed(true);
    }
  };

  const closeZoom = () => {
    setIsZoomed(false);
  };

  useEffect(() => {
    const handleScroll = () => {
      setIsButtonsVisible(false);
    };

    const handleClickOutside = (event) => {
      if (galleryRef.current && !galleryRef.current.contains(event.target)) {
        setIsButtonsVisible(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    galleryRef.current.addEventListener('scroll', handleScroll);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      galleryRef.current.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
      <div className="image-gallery" ref={galleryRef}>
        {images.map((imageURL, index) => (
            <div key={index} className={`image-wrapper ${selectedImg === index ? 'selected' : ''}`}
                 onClick={(e) => handleImageClick(index, e)}>
              <img src={imageURL} alt={`Gallery ${index}`} className="gallery-image" />
            </div>
        ))}
        {selectedImg !== null && isButtonsVisible && (
            <div className="buttons" style={buttonStyle}>
              <button className="image-button" onClick={handleZoomImage}>Zoom</button>
              <button className="image-button" onClick={handleAddClick}>Add to Library</button>
            </div>
        )}
        {isZoomed && (
            <>
              <div className="overlay" onClick={closeZoom}></div>
              <div className="zoomed-image-container">
                <img src={images[selectedImg]} alt={`Zoomed ${selectedImg}`} className="zoomed-image" />
                <button className="close-button" onClick={closeZoom}>X</button>
              </div>
            </>
        )}
      </div>
  );
};

export default ShowImg;
