import React, { useRef, useEffect } from 'react';
import { fabric } from 'fabric';

const Remake = () => {
    const canvasRef = useRef(null);
    const canvasSize = { width: 512, height: 512 };

    useEffect(() => {
        const canvas = new fabric.Canvas(canvasRef.current, {
            width: canvasSize.width,
            height: canvasSize.height,
            backgroundColor: 'white',
        });

        // 设置画布的缩放和拖拽功能
        canvas.setZoom(1);
        canvas.setViewportTransform([1, 0, 0, 1, 0, 0]);
        canvas.selection = false;

        // 添加一个矩形作为示例
        const rect = new fabric.Rect({
            left: 100,
            top: 100,
            fill: 'blue',
            width: 20,
            height: 20,
            angle: 0,
            padding: 10,
            transparentCorners: false,
        });
        canvas.add(rect);

        // 添加一个旋转按钮
        const rotateButton = document.createElement('button');
        rotateButton.textContent = 'Rotate';
        rotateButton.onclick = () => {
            rect.angle += 90;
            canvas.requestRenderAll();
        };
        canvasRef.current.parentNode.appendChild(rotateButton);

        // 添加一个缩放按钮
        const zoomInButton = document.createElement('button');
        zoomInButton.textContent = 'Zoom In';
        zoomInButton.onclick = () => {
            canvas.setZoom(canvas.getZoom() * 1.1);
            canvas.requestRenderAll();
        };
        canvasRef.current.parentNode.appendChild(zoomInButton);

        const zoomOutButton = document.createElement('button');
        zoomOutButton.textContent = 'Zoom Out';
        zoomOutButton.onclick = () => {
            canvas.setZoom(canvas.getZoom() / 1.1);
            canvas.requestRenderAll();
        };
        canvasRef.current.parentNode.appendChild(zoomOutButton);
    }, []);

    return (
        <div
            style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                overflow: 'hidden',
                height: '60vh',
                width:'80vh',
                position: 'relative',
            }}
        >
            <canvas
                ref={canvasRef}
                style={{ border: '1px solid black' }}
            />
        </div>
    );
};

export default Remake;
