import React, { useState, useEffect } from 'react';
import './loadingicon.css';

function LoadingAnimation() {
    const [iconClass, setIconClass] = useState('icon-1-2s'); // ��ʼΪ��1-2���ͼ��

    useEffect(() => {
        let timeoutId;
        const interval = setInterval(() => {
            setIconClass('icon-3s'); // 应用快速旋转动画和更新的SVG图标

            timeoutId = setTimeout(() => {
                setIconClass(''); // 回到没有旋转的初始状态
            }, 1000); // 1秒后（第3秒结束时）
        }, 3000); // 每3秒循环一次

        return () => {
            clearInterval(interval);
            clearTimeout(timeoutId);
        };
    }, []);
    return (
        // <div className="loading-animation-container">
            <div className="backboard">
                <div className="loading-container">
                    <div className="loader-icon"></div>
                    {iconClass === 'icon-3s' ? (
                       
                        <svg width="68" height="39" viewBox="0 0 68 39" fill="none" xmlns="http://www.w3.org/2000/svg">...</svg>
                    ) : (
                        /* 其他时间的SVG */
                        <svg width="68" height="23" viewBox="0 0 68 23" fill="none" xmlns="http://www.w3.org/2000/svg">...</svg>
                    )}
                    <div className="icontext">图片生成中......</div>
                </div>
            </div>
        // </div>


    );
}

export default LoadingAnimation;
