import React, { useEffect, useRef, useState } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = ({ setDataUrl,dataUrl }) => {
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);


    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleDrop = (e) => {
        e.preventDefault();
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        if (type === 'library') {
            fabric.Image.fromURL(
                url,
                (img) => {
                    img.set({
                        selectable: true,
                        scalable: true,
                        hasControls: true,
                    });
                    img.scale(0.25);
                    img.setCoords();
                    addDeleteButton(img); // 添加删除按钮
                    fabricCanvasRef.current.add(img);
                },
                { crossOrigin: 'anonymous' }
            );
        }
    };

    const addDeleteButton = (obj) => {
        const deleteButton = new fabric.Text('x', {
            fontFamily: 'Arial',
            fontSize: 20,
            fill: 'red',
            originX: 'right',
            originY: 'top',
            selectable: false,
            evented: false,
        });
        deleteButton.set({
            top: obj.top,
            left: obj.left + obj.width,
        });
        deleteButton.on('mousedown', () => {
            fabricCanvasRef.current.remove(obj);
            fabricCanvasRef.current.remove(deleteButton);
        });
        obj.deleteButton = deleteButton;
        fabricCanvasRef.current.add(deleteButton);
        fabricCanvasRef.current.bringToFront(deleteButton);
    };

    useEffect(() => {
        if (!fabricCanvasRef.current&& !dataUrl) {
            fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
                width: window.innerHeight * 0.6,
                height: window.innerHeight * 0.6,
                selection: true,
                preserveObjectStacking: true,
                interactive: true,
            });
        }
        else if (dataUrl) {
            // 如果dataurl不为空，则使用dataurl来渲染画布
            fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
                width: window.innerHeight * 0.6,
                height: window.innerHeight * 0.6,
                selection: true,
                preserveObjectStacking: true,
                interactive: true,
            });
            fabric.Image.fromURL(dataUrl, (img) => {
                fabricCanvasRef.current.add(img);
            });

        }



        fabricCanvasRef.current.on('selection:cleared', removeDeleteButton);

        return () => {
            fabricCanvasRef.current.off('selection:cleared', removeDeleteButton);
        };

    }, []);

    useEffect(() => {
        const saveCanvasToDataUrl = () => {
            if (fabricCanvasRef.current) {
                // 创建一个512*512的矩形剪切区域
                const clipRect = new fabric.Rect({
                    left: 1, // 左边框宽度
                    top: 1, // 上边框宽度
                    width: 510, // 512 - 左右边框宽度
                    height: 510, // 512 - 上下边框宽度
                    fill: '', // 透明
                    strokeWidth: 0, // 无边框
                    selectable: false, // 不可选中
                    evented: false, // 不响应事件
                });
                // 将矩形剪切区域设置为画布的裁剪区域
                fabricCanvasRef.current.clipPath = clipRect;

                // 获取被裁剪区域内的图案的dataURL
                const dataURL = fabricCanvasRef.current.toDataURL({
                    format: 'png',
                    multiplier: 1,
                    quality: 1
                });

                // 重置画布的裁剪区域
                fabricCanvasRef.current.clipPath = null;


                setDataUrl(dataURL);
            }
        };

        saveCanvasToDataUrl();

        const canvasStateChangeListener = () => {
            saveCanvasToDataUrl();
        };

        fabricCanvasRef.current.on('object:added', canvasStateChangeListener);
        fabricCanvasRef.current.on('object:removed', canvasStateChangeListener);

        return () => {
            fabricCanvasRef.current.off('object:added', canvasStateChangeListener);
            fabricCanvasRef.current.off('object:removed', canvasStateChangeListener);
        };
    }, [setDataUrl]);


    const removeDeleteButton = () => {
        fabricCanvasRef.current.getObjects().forEach(obj => {
            if (obj.deleteButton) {
                fabricCanvasRef.current.remove(obj.deleteButton);
                delete obj.deleteButton;
            }
        });
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh', position: 'relative', backgroundColor: 'white' }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
        </div>
    );
};

export default FabricCanvas;
