import React, { useState, useRef, useImperativeHandle, forwardRef, useCallback, useEffect } from 'react';
import StepBar from '../../components/StepBar/StepBar';
import './MakeContinuous.css';
import ColorSelector from '../../components/ColorSelector.js/ColorSelector';
import ConnectionSelector from '../../components/ColorSelector.js/ConnectionSelector';
import ContinuousPatterns from '../../components/ContinuousPatterns/ContinuousPatterns';


const MakeContinuous = forwardRef((props, ref) => {
  const canvasRef = useRef(null);

  useImperativeHandle(ref, () => ({
      clearCanvas() {
          const canvas = canvasRef.current;
          if (!canvas) return;
          const context = canvas.getContext('2d');
          context.clearRect(0, 0, canvas.width, canvas.height);
      }
    
  }));
  const [connectionType, setConnectionType] = useState('ƽ��');
  // �� props �н⹹����Ҫ�Ĳ���
  const { currentStep, handleNextStep, handlePrevStep, sketchDataURL, onSave,spacing,setSpacing,selectedColor,setSelectedColor} = props;
  // const [selectedColor, setSelectedColor] = useState('#FFFFFF'); // Ĭ��Ϊ��ɫ
  const handleConnectionTypeChange = useCallback((newType) => {
    setConnectionType(newType);
  }, []);


  return (
    <div className="container containerColumn" style={props.style}>
      
        <StepBar currentStep={currentStep} handleNextStep={handleNextStep} handlePrevStep={handlePrevStep} />
        <div className="middle">
          <ContinuousPatterns spacing={spacing} setSpacing={setSpacing} onSave={onSave} sketchDataURL={sketchDataURL} selectedColor={selectedColor} connectionType={connectionType} ref={ref} />
        </div>
        <div className="bottom">
        <div className="selectors-wrapper">
 <ColorSelector onColorSelect={setSelectedColor} selectedColor={selectedColor}/>
      <ConnectionSelector selectedConnection={connectionType} onConnectionTypeChange={handleConnectionTypeChange}/>
      
    </div>
        </div>
       
      
    </div>
  );
})

export default MakeContinuous;
