// export default FabricCanvas;
import React, { useRef, useEffect } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = new fabric.Canvas(canvasRef.current);

        // 添加删除按钮
        function addDeleteButton(object) {
            var deleteButton = new fabric.Rect({
                left: object.left + object.width / 2 - 10,
                top: object.top - object.height / 2 - 10,
                width: 20,
                height: 20,
                fill: 'red',
                selectable: false
            });

            deleteButton.on('mousedown', function() {
                canvas.remove(object);
                canvas.remove(deleteButton);
                canvas.renderAll();
            });

            canvas.add(deleteButton);
        }

        // 监听选定对象的事件，在选定对象时添加删除按钮
        canvas.on('selection:created', function(event) {
            var selectedObject = event.target;
            addDeleteButton(selectedObject);
        });

        // 监听选定对象更新的事件，在更新时移除之前的删除按钮并添加新的删除按钮
        canvas.on('selection:updated', function(event) {
            var selectedObject = event.target;
            canvas.remove(canvas.item(canvas.getObjects().length - 1)); // 移除之前的删除按钮
            addDeleteButton(selectedObject);
        });

        // 监听取消选定对象的事件，在取消选定时移除删除按钮
        canvas.on('selection:cleared', function(event) {
            canvas.remove(canvas.item(canvas.getObjects().length - 1)); // 移除删除按钮
        });

        // 创建一个矩形对象
        var rect = new fabric.Rect({
            left: 100,
            top: 100,
            width: 50,
            height: 50,
            fill: 'red'
        });
        canvas.add(rect);

        return () => {
            canvas.dispose(); // 清理 Fabric.js 实例
        };
    }, []);

    return <canvas ref={canvasRef} width={400} height={400}></canvas>;
};

export default FabricCanvas;
