// export default FabricCanvas;
import React, { useEffect, useRef } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = ({ targets, setTargets }) => {
    const canvasRef = useRef(null);

    useEffect(() => {
        if (!canvasRef.current) return;

        const canvas = new fabric.Canvas(canvasRef.current, {
            width: 600,
            height: 400,
            selection: false
        });

        // 添加已存在的targets到canvas
        targets.forEach(target => {
            addImageToCanvas(target);
        });

        // 监听targets的变化
        return () => {
            canvas.dispose();
        };
    }, [canvasRef, targets]);

    // 将图片添加到canvas中
    const addImageToCanvas = (target) => {
        fabric.Image.fromURL(
            target.src,
            (img) => {
                img.set({
                    left: target.x,
                    top: target.y,
                    width: target.width,
                    height: target.height,
                    angle: target.rotate,
                    hasControls: true,
                    hasBorders: true,
                    cornerColor: 'black',
                    cornerSize: 10,
                    transparentCorners: false,
                    selectable: true,
                    originX: 'center',
                    originY: 'center',
                    lockRotation: false
                });
                canvas.add(img);
            },
            { crossOrigin: 'anonymous' }
        );
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleDrop = (e) => {
        e.preventDefault();
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        if (type === 'library') {
            // 新添加的图片对象
            const newTarget = { src: url, x: 100, y: 100, width: 200, height: 150, rotate: 0 };
            setTargets([...targets, newTarget]);
        }
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            style={{ border: '1px solid black', position: 'relative' }}
        >
            <canvas
                ref={canvasRef}
                width={600}
                height={400}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
        </div>
    );
};

export default FabricCanvas;
