import React, { useEffect, useRef } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = new fabric.Canvas(canvasRef.current, {
            width: 600,
            height: 400,
            selection: false // 关闭默认选择框
        });

        // 创建一个可交互的图片对象
        fabric.Image.fromURL(
            'https://via.placeholder.com/200', // 示例图片URL
            (img) => {
                img.set({
                    left: 100,
                    top: 100,
                    width: 200, // 设置图片宽度
                    height: 150, // 设置图片高度
                    hasControls: true, // 启用控制点
                    hasBorders: true, // 启用边框
                    cornerColor: 'black',
                    cornerSize: 10,
                    transparentCorners: false,
                    selectable: true, // 允许选择
                    originX: 'center', // 设置旋转和缩放的中心点
                    originY: 'center',
                    lockRotation: false // 允许旋转
                });
                canvas.add(img); // 添加图片到Canvas
            },
            { crossOrigin: 'anonymous' } // 允许跨域请求
        );

        return () => {
            canvas.dispose(); // 清理Canvas
        };
    }, []); // 空依赖数组意味着这个effect只会在组件挂载和卸载时运行

    return (
        <canvas
            ref={canvasRef}
            style={{ border: '1px solid black' }}
        ></canvas>
    );
};

export default FabricCanvas;
