import React, { useEffect, useRef, useState } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);

    useEffect(() => {
        // 娣诲姞浜嬩欢鐩戝惉鍣�
        const canvas = canvasRef.current;
        if (canvas) {
            canvas.addEventListener('dragover', handleDragOver);
            canvas.addEventListener('drop', handleDrop);
        }


        return () => {
            // 绉婚櫎浜嬩欢鐩戝惉鍣�
            // eslint-disable-next-line react-hooks/exhaustive-deps
            const canvas = canvasRef.current;
            if (canvas) {
                canvas.removeEventListener('dragover', handleDragOver);
                canvas.removeEventListener('drop', handleDrop);
            }
        };
    }, []); // 绌轰緷璧栨暟缁勬剰鍛崇潃杩欎釜effect鍙�浼氬湪缁勪欢鎸傝浇鍜屽嵏杞芥椂杩愯��

    useEffect(() => {// 鍒涘缓涓€涓�姝ｆ柟褰�
        if (!fabricCanvasRef.current) {
            fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
                width: window.innerHeight * 0.6,
                height: window.innerHeight * 0.6,
                selection: true,
                preserveObjectStacking: true,
                interactive: true,
            });
        }

        // 娣诲姞榧犳爣浜嬩欢鐩戝惉鍣�
        fabricCanvasRef.current.on('mouse:down', (options) => {
            if (options.target) {
                options.target.opacity = 1;
            }
        });

        fabricCanvasRef.current.on('mouse:up', (options) => {
            if (options.target) {
                options.target.opacity = 1;
            }
        });

        fabricCanvasRef.current.on('object:moving', (options) => {
            options.target.opacity = 1;
        });

        fabricCanvasRef.current.on('object:modified', (options) => {
            options.target.opacity = 1;
        });
    }, []);

    // 澶勭悊dragover浜嬩欢
    const handleDragOver = (e) => {
        e.preventDefault();
    };

    // 澶勭悊drop浜嬩欢
    const handleDrop = (e) => {
        e.preventDefault();
        // 鑾峰彇鎷栨嫿鐨勬暟鎹�
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        // 濡傛灉鏄�鏉ヨ嚜绱犳潗搴撶殑鎷栨嫿鏁版嵁
        if (type === 'library') {
            // 鍒涘缓fabric.Image瀵硅薄
            fabric.Image.fromURL(
                url,
                (img) => {
                    // 璁剧疆鍥剧墖涓哄彲閫夋嫨鍜屽彲缂╂斁
                    img.set({
                        selectable: true,
                        scalable: true,
                        hasControls: true,
                    });

                    // 缂╂斁鍥剧墖鍒板師鏉ョ殑鍥涘垎涔嬩竴澶у皬
                    img.scale(0.25);

                    // 璁剧疆鍥剧墖浣嶇疆涓虹敾甯冪殑鍘熺偣
                    img.setCoords();

                    // 娣诲姞鍥剧墖鍒扮敾甯�
                    fabricCanvasRef.current.setActiveObject(img); // 璁剧疆涓烘椿鍔ㄥ�硅薄
                    fabricCanvasRef.current.add(img);
                    fabricCanvasRef.current.renderAll();

                },
                { crossOrigin: 'anonymous' }
            ); // 鍏佽�歌法鍩熻�锋眰
        }
    };

    // 澶勭悊鍒犻櫎瀵硅薄
    const handleDeleteObject = () => {
        const activeObject = fabricCanvasRef.current.getActiveObject();
        if (activeObject) {
            fabricCanvasRef.current.remove(activeObject);
            fabricCanvasRef.current.requestRenderAll(); // 閲嶆柊娓叉煋鐢诲竷
        }
    };



    return (
        <div
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh', position: 'relative',backgroundColor:'white' }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
            <button onClick={handleDeleteObject} style={{ position: 'absolute', top: '10px', right: '10px' }}>
                Delete Object
            </button>
        </div>
    );
};

export default FabricCanvas;
