import React, { useEffect, useRef, useState } from 'react';
import './ShowImg.css';



const ShowImg = ({ onAddToLibrary,images,onRefresh,showImages,isClicked }) => {
  const [selectedImg, setSelectedImg] = useState(null);
  const [buttonStyle, setButtonStyle] = useState({});
  const [isZoomed, setIsZoomed] = useState(false);
  const galleryRef = useRef(null);
  const [isButtonsVisible, setIsButtonsVisible] = useState(true);
  const [addedImages, setAddedImages] = useState([]);
  const [threeisClicked, setthreeIsClicked] = useState(false);
  const handleImageClick = (index, event) => {
    setSelectedImg(index);
    setIsZoomed(false);
    setIsButtonsVisible(true);
    const rect = event.target.getBoundingClientRect();
    const isLeftSide = index % 2 === 0;
    let buttonLeftStyle = isLeftSide ? rect.left - 160 + 'px' : rect.right -10  + 'px';

    setButtonStyle({
      position: 'absolute',
      top: `${rect.top}px`,
      left: buttonLeftStyle,
    });
  };

  const handleZoomImage = () => {
    if (selectedImg !== null) {
      setIsZoomed(true);
    }
  };

const handleAddClick = () => {
  setthreeIsClicked(true);
  setTimeout(() => setthreeIsClicked(false), 200);
  if (onAddToLibrary && selectedImg !== null) {
    const imageUrl = images[selectedImg];
    if (!addedImages.includes(imageUrl)) {
      onAddToLibrary(imageUrl);
      setAddedImages([...addedImages, imageUrl]);
    } else {

      alert("不可重复添加");
    }
  }
};
const handleClick=()=>{
  setthreeIsClicked(true);
  setTimeout(() => setthreeIsClicked(false), 200);
}
  const closeZoom = () => {
    setIsZoomed(false);
  };

  useEffect(() => {

    const handleScroll = () => {
      setIsButtonsVisible(false);
    };


    const handleClickOutside = (event) => {
      if (galleryRef.current && !galleryRef.current.contains(event.target)) {
        setIsButtonsVisible(false);
      }
    };


    const galleryElement = galleryRef.current;

    galleryElement.addEventListener('scroll', handleScroll);


    document.addEventListener('mousedown', handleClickOutside);


    galleryElement.scrollTop = galleryElement.scrollHeight;


    return () => {
      galleryElement.removeEventListener('scroll', handleScroll);
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  useEffect(() => {
    if (galleryRef.current && images.length > 0) {
      const nodes = galleryRef.current.querySelectorAll('.image-wrapper');
      if (nodes.length > 0) {
        const lastImageElement = nodes[nodes.length - 1];
        lastImageElement.scrollIntoView({ behavior: 'smooth' });
      }
    }
  }, [images.length]); // Only re-run the effect if images array length changes


  return (
      <div className="image-gallery" ref={galleryRef}>
        {(images || []).map((imageURL, index) => (
            <div
                className={`image-wrapper ${selectedImg === index ? 'selected' : ''}`}
                key={index}
                onMouseEnter={(e) => handleImageClick(index, e)}
            >
              <img src={imageURL} alt={`Gallery ${index + 1}`} className="gallery-image"/>
            </div>
        ))}
        {selectedImg !== null && isButtonsVisible && (
            <div className="buttons" style={buttonStyle} >
              <button className="image-button" onClick={handleClick}>
                {threeisClicked ?
                    <img href="/icon/variation.svg"  /> : // 点击时的图标icon2
                    <img href="/icon/variationafter.svg"  />   // 默认的图标icon1
                }

              </button>
              <button className="image-button" onClick={handleAddClick}>
                <svg width="13" height="13" viewBox="0 0 23 21" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path
                      d="M10.6033 0.816987C10.9701 0.0737418 12.0299 0.0737396 12.3967 0.816985L14.941 5.9722C15.0866 6.26734 15.3682 6.47191 15.6939 6.51924L21.383 7.34592C22.2033 7.46511 22.5308 8.47308 21.9373 9.05161L17.8206 13.0644C17.5849 13.2941 17.4773 13.6251 17.533 13.9495L18.5048 19.6157C18.6449 20.4326 17.7875 21.0555 17.0538 20.6698L11.9653 17.9946C11.674 17.8415 11.326 17.8415 11.0347 17.9946L5.94616 20.6698C5.21253 21.0555 4.3551 20.4326 4.49521 19.6157L5.46702 13.9495C5.52266 13.6251 5.41511 13.2941 5.17943 13.0644L1.06274 9.05161C0.469227 8.47308 0.796736 7.46511 1.61696 7.34592L7.30608 6.51924C7.63178 6.47191 7.91335 6.26734 8.05901 5.9722L10.6033 0.816987Z"
                      fill="currentColor"/>
                </svg>
                作为素材
              </button>
              <button className="image-button" onClick={handleZoomImage}>
                <svg width="13" height="13" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path
                      d="M16.2508 8.12381C16.2508 9.91652 15.6687 11.5725 14.6882 12.9161L19.6338 17.8646C20.1221 18.3528 20.1221 19.1456 19.6338 19.6338C19.1455 20.1221 18.3525 20.1221 17.8642 19.6338L12.9186 14.6853C11.5748 15.6696 9.91845 16.2476 8.1254 16.2476C3.6369 16.2476 0 12.6114 0 8.12381C0 3.63619 3.6369 0 8.1254 0C12.6139 0 16.2508 3.63619 16.2508 8.12381ZM7.18785 11.5608C7.18785 12.0803 7.60584 12.4982 8.1254 12.4982C8.64495 12.4982 9.06294 12.0803 9.06294 11.5608V9.06117H11.5631C12.0826 9.06117 12.5006 8.64326 12.5006 8.12381C12.5006 7.60435 12.0826 7.18645 11.5631 7.18645H9.06294V4.68681C9.06294 4.16736 8.64495 3.74945 8.1254 3.74945C7.60584 3.74945 7.18785 4.16736 7.18785 4.68681V7.18645H4.68773C4.16817 7.18645 3.75018 7.60435 3.75018 8.12381C3.75018 8.64326 4.16817 9.06117 4.68773 9.06117H7.18785V11.5608Z"
                      fill="currentColor"/>
                </svg>
                放大查看
              </button>
            </div>
        )}
        {isZoomed && (
            <>
              <div className="overlay" onClick={closeZoom}></div>
              <div className="zoomed-image-container">
                <img
                    src={images[selectedImg]}
                    alt={`Zoomed ${selectedImg + 1}`}
                    className="zoomed-image"
                />
                <button className="close-button" onClick={closeZoom}>X</button>
              </div>
            </>
        )}
        {showImages && <button className="regenerate-button" onClick={onRefresh}>
          {isClicked ? <img src="/icon/redoafter.svg" alt="Icon 1"/> : <img src="/icon/redobefor.svg" alt="Icon 2"/>}
        </button>}
      </div>

  );
};

export default ShowImg;
