import React, { useRef,useState } from 'react';
import './ShowClothes.css';
import StepBar from '../../components/StepBar/StepBar';
import ClothBox from './ClothBox';

const ShowClothes = ({ setSpacing,spacing,currentStep, handleNextStep, handlePrevStep, sketchDataURL, patternDataURL,setConnectionType,connectionType }) => {

    const clothBoxRef = useRef(null);

    const handleSaveImage = () => {
        if (clothBoxRef.current) {
            clothBoxRef.current.saveCanvasAsImage();
        }
    };
    const increaseSpacing = () => {
        setSpacing(prevSpacing => prevSpacing + 100);
        // console.log(spacing);
    };

    // 减少间距
    const decreaseSpacing = () => {
        setSpacing(prevSpacing => prevSpacing - 100 >= 0 ? prevSpacing - 100 : 0);
        // console.log(spacing);
    };

    return (
        <div className="container containerColumn">
            <StepBar currentStep={currentStep} handleNextStep={handleNextStep} handlePrevStep={handlePrevStep} />
            <div className="middlecloth" >
                    <div className="clothbox"> <ClothBox key={patternDataURL} setConnectionType={setConnectionType} connectionType={connectionType} spacing={spacing} ref={clothBoxRef} sketchDataURL={sketchDataURL} patternDataURL={patternDataURL} /></div>
                   <div className="clothbox"> <ClothBox  setConnectionType={setConnectionType} connectionType={connectionType} spacing={spacing} ref={clothBoxRef} sketchDataURL={sketchDataURL} patternDataURL={patternDataURL} /></div>
            </div>
            <div className="bottom">
                <div className="containerbox">
                    <span className="text-above-buttons">缩放纹样</span>
                    <div className="button-group">
                        <button className="bottom-buttonbig" onClick={increaseSpacing}>
                            <svg width="20" height="20" viewBox="0 0 20 20" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <path d="M2 10H18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                                <path d="M10 2L10 18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                            </svg>
                        </button>
                        <button className="bottom-buttonbig" onClick={decreaseSpacing}>
                            <svg width="20" height="4" viewBox="0 0 20 4" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <path d="M2 2H18" stroke="white" stroke-width="3" stroke-linecap="round"
                                      stroke-linejoin="round"/>
                            </svg>
                        </button>
                    </div>
                    <button className="bottom-button" onClick={handleSaveImage}>输出印花文件</button>
                </div>

            </div>
        </div>
    );
};

export default ShowClothes;
