import React, { useEffect, useRef } from 'react';
import { fabric } from 'fabric';

const FabricCanvas = () => {
    const canvasRef = useRef(null);
    const fabricCanvasRef = useRef(null);

    useEffect(() => {
        // 初始化canvas
        const canvas = canvasRef.current;
        if (canvas) {
            canvas.addEventListener('dragover', handleDragOver);
            canvas.addEventListener('drop', handleDrop);
        }

        return () => {
            // 清理canvas事件监听
            const canvas = canvasRef.current;
            if (canvas) {
                canvas.removeEventListener('dragover', handleDragOver);
                canvas.removeEventListener('drop', handleDrop);
            }
        };
    }, []);

    useEffect(() => {
        // 初始化fabric canvas
        if (!fabricCanvasRef.current) {
            fabricCanvasRef.current = new fabric.Canvas(canvasRef.current, {
                width: window.innerHeight * 0.6,
                height: window.innerHeight * 0.6,
                selection: true,
                preserveObjectStacking: true,
                interactive: true,
            });
        }
    }, []);

    // 处理dragover事件
    const handleDragOver = (e) => {
        e.preventDefault();
    };

    // 处理drop事件
    const handleDrop = (e) => {
        e.preventDefault();
        const dragData = e.dataTransfer.getData('application/json');
        const { type, url } = JSON.parse(dragData);

        if (type === 'library') {
            fabric.Image.fromURL(
                url,
                (img) => {
                    img.set({
                        selectable: true,
                        scalable: true,
                        hasControls: true,
                    });
                    img.scale(0.25);
                    img.setCoords();

                    // 创建删除按钮
                    const deleteButton = new fabric.IText('X', {
                        left: img.left + img.width - 15,
                        top: img.top - 15,
                        fill: 'red',
                        fontSize: 12,
                        hasControls: false,
                        hasBorders: false,
                        hoverCursor: 'pointer',
                        selectable: false,
                        evented: false, // 设置为不可交互
                    });

                    // 添加点击事件监听
                    deleteButton.on('mousedown', () => {
                        fabricCanvasRef.current.remove(img);
                        fabricCanvasRef.current.requestRenderAll();
                    });

                    // 将删除按钮添加到图片对象中
                    img.addWithUpdate(deleteButton);

                    // 将图片添加到画布
                    fabricCanvasRef.current.add(img);
                    fabricCanvasRef.current.renderAll();
                },
                { crossOrigin: 'anonymous' }
            );
        }
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            style={{ border: '1px solid black', maxWidth: '60vh', maxHeight: '60vh', position: 'relative', backgroundColor: 'white' }}
        >
            <canvas
                ref={canvasRef}
                width={512}
                height={512}
                style={{ width: '100%', height: '100%' }}
            ></canvas>
        </div>
    );
};

export default FabricCanvas;
